// contains hardocded weights and biases for each neuron. And LFSR seeds if used
// needs SNGs to generate weights and biases.
//
// Inputs: stochastic input data
// Outputs: stochastic results x32

module Layer2(
    input clk,
    input reset,
    input data_in_stoch     [0:195],
    
    output macc_results     [0:31],
    output results          [0:31]
    );

    localparam NUM_NEUR = 32;
    localparam NUM_INP = 196;

    // Wires for SNGs -> Neurons
