                .reset              (reset),
                .seed               (adder_seeds[i]),
                .prob               (16'h8000),     // 0.5
                .stoch_num          (add_sel_stoch[i])
            );
        end
    endgenerate
    //////////////////////////////////////////////////////////////


    // Generate 32 neurons
    generate
        for (i=0; i<NUM_NEUR; i=i+1) begin
            Neuron196_L2 neur_l2(
                .clk                (clk),
                .reset              (reset),
                .input_data         (data_in_stoch),
